import "core-js/modules/es.array.push.js";
import request from "@/utility/request";
export default {
  data() {
    return {
      sizeForm: {
        userName: '',
        password: '',
        email: '',
        // 账户类型，消费者||商家
        accountType: '消费者'
      },
      rules: {
        userName: [{
          required: true,
          message: '用户名不能为空',
          trigger: 'blur'
        }, {
          min: 3,
          max: 20,
          message: '长度在 3 到 20 个字符',
          trigger: 'blur'
        }],
        password: [{
          required: true,
          message: '密码不能为空',
          trigger: 'blur'
        }, {
          min: 8,
          max: 20,
          message: '长度在 8 到 20 个字符',
          trigger: 'blur'
        }],
        email: [{
          required: true,
          message: '邮箱不能为空',
          trigger: 'blur'
        }, {
          type: "email",
          required: true,
          message: '邮箱格式错误',
          trigger: ['blur', 'change']
        }]
      }
    };
  },
  methods: {
    onSubmit(formName) {
      return request.post("http://localhost:8080/user/register", this.sizeForm).then(result => {
        if (result.code === 0) {
          //存在重复用户名，重新注册
          this.$message.error("存在重复用户名，请重新注册");
        } else {
          //注册成功
          this.$message.success("注册成功,请登录账号");
          this.$router.push('/rl/login');
        }
      });
    }
  }
};
