import "core-js/modules/es.array.push.js";
import request from "@/utility/request";
export default {
  data() {
    return {
      //用户信息表单
      sizeForm: {
        userName: '',
        password: '',
        gender: '',
        age: '',
        email: '',
        phoneNumber: ''
      },
      rules: {
        userName: [{
          required: true,
          message: '用户名不能为空',
          trigger: 'blur'
        }, {
          min: 3,
          max: 20,
          message: '长度在 3 到 20 个字符',
          trigger: 'blur'
        }],
        password: [{
          required: true,
          message: '密码不能为空',
          trigger: 'blur'
        }, {
          min: 8,
          max: 20,
          message: '长度在 8 到 20 个字符',
          trigger: 'blur'
        }],
        age: [{
          type: "number",
          message: '年龄必须为数字值',
          trigger: 'blur'
        }, {
          validator: (rule, value, callback) => {
            //验证数值是否为合法范围
            if (value < 0 || value > 200) {
              callback(new Error("年龄数值错误"));
            }
            callback();
          }
        }],
        email: [{
          required: true,
          message: '邮箱不能为空',
          trigger: 'blur'
        }, {
          type: "email",
          required: true,
          message: '邮箱格式错误',
          trigger: ['blur', 'change']
        }]
      }
    };
  },
  //通过令牌访问个人信息
  created() {
    this.sizeForm.userName = this.$parent.userName;
    //获取个人信息表单
    request.post("http://localhost:8080/user/selectInfo", this.sizeForm.userName).then(result => {
      // result = Result类对象，其data字段为user类对象
      let data = result.data;
      if (data != null) {
        this.sizeForm = data;
      } else {
        //说明令牌失效或者不存在该令牌
        localStorage.removeItem('token');
        localStorage.removeItem('userName');
        localStorage.removeItem('userID');
        localStorage.removeItem('accountType');
        // 重定向到登录界面
        this.$router.push('/rl/login');
      }
    });
  },
  methods: {
    onSubmit(formName) {
      this.$refs[formName].validate(valid => {
        if (valid) {
          request.post("http://localhost:8080/user/updateInfo", this.sizeForm).then(result => {
            let msg = result.msg;
            this.$message.info(msg);
          });
        } else {
          this.$message.error("表单验证不通过，请重试");
        }
      });
    }
  }
};
